import  React, { Component } from  'react';
import  PostService  from  './PostService';
import $ from 'jquery';
import {Form,Col,Button} from 'react-bootstrap'
import bsCustomFileInput from 'bs-custom-file-input'

const  postService  =  new  PostService();


class  UploadPost  extends  Component {




constructor(props) {
    super(props);

    this.state = {
      Name: "",
      Init_time: "",
      Author: "",
      Description: "",
      Video: null,
      Image: null,
      Doc: null

      };
this.handleSubmit = this.handleSubmit.bind(this)
}

componentDidMount() {

  $(document).ready(function () {
    bsCustomFileInput.init()
  })
}

handleChange = (e) => {
  this.setState({
    [e.target.id]: e.target.value
  })
  console.log("blah")
};

handleFileChange = (e) => {
  this.setState({
  [e.target.label]: e.target.files[0]

  })
  console.log(e.target.id)
};

handleSubmit = (e) => {
  var today = new Date()
  var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate() + "T" + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() +"Z";

  let form_data = new FormData();

  form_data.append('Name', this.state.Name);
  form_data.append('Init_time', date);
  form_data.append('Author', this.state.Author);
  form_data.append('Description', this.state.Description);
  if (this.state.Video != null) {
    form_data.append('Video', this.state.Video, this.state.Video.name);
  }
if (this.state.Image != null) {
  form_data.append('Image', this.state.Image, this.state.Image.name);

}
if (this.state.Doc != null) {
  form_data.append('Doc', this.state.Doc, this.state.Doc.name);
}


console.log(form_data)
postService.createPost(form_data)

  e.preventDefault()




};


onChangeHandler=event=>{

}

render() {

  return (

  <div className="container">
  <Form onSubmit =  {this.handleSubmit}>
    <Form.Row>
      <Form.Group as={Col} controlId="formGridEmail">
        <Form.Label>Title</Form.Label>
        <Form.Control type="text" placeholder="Enter email" ref="Name"
        onChange = {this.handleChange} />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridPassword">
        <Form.Label>Author</Form.Label>
        <Form.Control type="text" placeholder="Password" ref="Author"
        onChange = {this.handleChange}/>
      </Form.Group>
    </Form.Row>

    <Form.Group controlId="formGridAddress1">
      <Form.Label>Description</Form.Label>
      <Form.Control placeholder="1234 Main St" ref="Description"
      onChange = {this.handleChange}/>
    </Form.Group>



    <Form.Row style={{marginTop:30}}>
      <Form.Group as={Col} controlId="formGridCity">
      <Form.File
      onChange = {this.handleFileChange}
         id="custom-file"
         label="Custom file input"
         data-browse="Add Img"
         custom
         ref = "Video"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridState">
      <Form.File
      onChange = {this.handleFileChange}
         id="custom-file"
         label="Custom file input"
         data-browse="Add Vid"
         custom
         ref = "Image"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridZip">
      <Form.File
        onChange = {this.handleFileChange}
         id="custom-file"
         label="Custom file input"
         data-browse="Add Doc"
         custom
         ref = "Doc"
       />
      </Form.Group>
    </Form.Row>


    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>
</div>

         );

  }
}
export  default  UploadPost;
