import  React, { Component } from  'react';
import  PostService  from  './PostService';
const  postService  =  new  PostService();
class  SearchPost  extends  Component {
  constructor(props) {
      super(props);
      this.state  = {
          posts: [],
      };
  }


  componentDidMount() {
    var self = this;
    const { match: { params } } = this.props;
    postService.searchPosts(params.query).then(function (result) {
        self.setState({ posts:  result.data})
        console.log(result.data)

    });
  }

  render() {
    return(             <div className="container">
                 <nav class="navbar navbar-expand-lg bg-transparent">
               <a class="navbar-brand" href="/">Home</a>
               <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                 <span class="navbar-toggler-icon"></span>
               </button>

             </nav>
        <div className="row">
        {this.state.posts.map( c =>
        <div key= {c.id} className="col-lg-8 col-md-10 mx-auto">
          <div className="post-preview">
            <a href={"/post/" + c.id}>
              <h2 className="post-title">
                {c.Name}
              </h2>
              <h3 className="post-subtitle">
                {c.Description.substring(0,10) + "..."}
              </h3>
            </a>
            <p className="post-meta">Posted by
                { " " + c.Author + " " }

              on {" " + c.Init_time.substring(0,10)}</p>
          </div>
          <hr></hr>


        </div>
        )}
        </div>
        <script type="text/javascript" src='https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.3.1/js/bootstrap.bundle.min.js' />
        </div>)
}

}

export  default  SearchPost;
