import  React, { Component } from  'react';
import  PostService  from  './PostService';
import $ from 'jquery';
import {Form,Col,Button,Navbar,Nav,NavDropdown} from 'react-bootstrap'
import { Formik } from 'formik'

import bsCustomFileInput from 'bs-custom-file-input'

const  postService  =  new  PostService();


class  UploadPost  extends  Component {




constructor(props) {
    super(props);

    this.state = {
      Name: "",
      Init_time: "",
      Author: "",
      Description: "",
      Video: null,
      Image: null,
      Doc: null

      };
this.handleSubmit = this.handleSubmit.bind(this)
}

componentDidMount() {

  $(document).ready(function () {
    bsCustomFileInput.init()
  })
}

handleChange = (e) => {
  this.setState({
    [e.target.id]: e.target.value
  })
  console.log("blah")
};

handleFileChange = (e) => {
  this.setState({
  [e.target.id]: e.target.files[0]

  })
  console.log(e.target.id)
};

handleSubmit = (e) => {
  var today = new Date()
  var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate() + "T" + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() +"Z";

  let form_data = new FormData();

  form_data.append('Name', this.state.Name);
  form_data.append('Init_time', date);
  form_data.append('Author', this.state.Author);
  form_data.append('Description', this.state.Description);
  if (this.state.Video != null) {
    form_data.append('Video', this.state.Video, this.state.Video.name);
  }
if (this.state.Image != null) {
  form_data.append('Image', this.state.Image, this.state.Image.name);

}
if (this.state.Doc != null) {
  form_data.append('Doc', this.state.Doc, this.state.Doc.name);
}


console.log(this.state)
postService.createPost(form_data)

  e.preventDefault()




};


onChangeHandler=event=>{

}




render() {

  const schema = yup.object({
    firstName: yup.string().required(),
    lastName: yup.string().required(),
    username: yup.string().required(),
    city: yup.string().required(),
    state: yup.string().required(),
    zip: yup.string().required(),
    terms: yup.bool().required(),
  });

  return (
  <body style={{paddingTop:0}}>
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
    <Navbar.Brand href="/">Home</Navbar.Brand>
    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
    <Navbar.Collapse id="responsive-navbar-nav">

      <Nav>
        <Nav.Link href="/posts/">Docs</Nav.Link>
        <Nav.Link href="/Upload/">
          Upload
        </Nav.Link>
      </Nav>
    </Navbar.Collapse>
  </Navbar>
  <div className="container">
<Formik>
  <Form style={{marginTop:50}} onSubmit =  {this.handleSubmit}>
<h1> Upload material</h1>
<br/>
    <Form.Row>
      <Form.Group as={Col} >

        <Form.Control type="text" placeholder="Email" id="Name"
        onChange = {this.handleChange} />
      </Form.Group>

      <Form.Group as={Col} >

        <Form.Control type="text" placeholder="Author" id="Author"
        onChange = {this.handleChange}/>
      </Form.Group>
    </Form.Row>

    <Form.Group >

      <Form.Control placeholder="Description" id="Description"
      onChange = {this.handleChange}/>
    </Form.Group>



    <Form.Row >
      <Form.Group as={Col} controlId="formGridCity">
      <Form.File
      onChange = {this.handleFileChange}
         id="Video"
         label="Video"
         data-browse="Add"
         custom
         ref = "Video"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridState">
      <Form.File
      onChange = {this.handleFileChange}
         id="Image"
         label="Image"
         data-browse="Add"
         custom
         ref = "Image"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridZip">
      <Form.File
        onChange = {this.handleFileChange}
         id="Doc"
         label="Docs"
         data-browse="Add"
         custom
         ref = "Doc"
       />
      </Form.Group>
    </Form.Row>
    <br/>

    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>
</Formik>
</div>
</body>
         );

  }
}
export  default  UploadPost;
