import  React, { Component } from  'react';
import  PostService  from  './PostService';

const  postService  =  new  PostService();

class  PostList  extends  Component {

constructor(props) {
    super(props);
    this.state  = {
        posts: [],
    };
}

componentDidMount() {
    var  self  =  this;
    const postService = new PostService();
    postService.getPosts().then(function (result) {
        console.log(result);
        self.setState({ posts:  result.data})
    });
}
render() {

  return (


         <div className="container">
           <nav className="navbar navbar-expand-lg bg-transparent">
         <a className="navbar-brand" href="/">Home</a>
         <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
           <span className="navbar-toggler-icon"></span>
         </button>
         <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
           <div className="navbar-nav">
             <a className="nav-item nav-link" href="docs.html">Docs<span className="sr-only">(current)</span></a>
             <a className="nav-item nav-link" href="upload.html">Upload</a>
           </div>
         </div>
         </nav>
         <div className="dropdown">
       <button type="button" className="btn dropdown-toggle" data-toggle="dropdown">
         Filter by
       </button>
       <div className="dropdown-menu">
         <a className="dropdown-item" href="/posts/videos/">Most Voted</a>
         <a className="dropdown-item" href="{% url 'most' %}">Actualized Posts</a>
       </div>
     </div>
<div className="row">
{this.state.posts.map( c =>
<div key= {c.id} className="col-lg-8 col-md-10 mx-auto">
  <div className="post-preview">
    <a href="#">
      <h2 className="post-title">
        {c.Name}
      </h2>
      <h3 className="post-subtitle">
        {c.Description.substring(0,10) + "..."}
      </h3>
    </a>
    <p className="post-meta">Posted by
        { " " + c.Author + " " }

      on {" " + c.Init_time.substring(0,10)}</p>
  </div>
  <hr></hr>


</div>
)};
</div>
<script type="text/javascript" src='https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.3.1/js/bootstrap.bundle.min.js' />
</div>
         );
  }
}
export  default  PostList;
