import  React, { Component } from  'react';
import  PostService  from  '../Action/PostService';
import {Link,withRouter} from 'react-router-dom'
import { connect } from 'react-redux';

import * as actions from '../../store/actions/auth';
import {Card,Nav,Navbar,NavDropdown} from 'react-bootstrap'
import {Form,Button} from 'react-bootstrap'
const  postService  =  new  PostService();

class  Homepage  extends  Component {
  constructor(props) {
      super(props);
      this.state = {
        Query: ""

        };
  }


  componentDidMount() {
    var self = this;


  }


  handleChange = (e) => {
    this.setState({
      Query: e.target.value
    })
  };
  render() {

    return (
    <body>
      <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
      <Navbar.Brand ><Link to="/">Home</Link></Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">

        <Nav>
          <Nav.Link ><Link to="/posts/">Docs</Link></Nav.Link>
          <Nav.Link ><Link to="/Upload/">
            Upload
          </Link></Nav.Link>
          <Nav.Link onClick={this.props.logout} >
              <Link to="/">Logout</Link>
            </Nav.Link>
        </Nav>
      </Navbar.Collapse>
    </Navbar>
      <div className="jumbotron container ">

        <h1>Learning Management System</h1>
        <h8>This webapp is aimed to cater to the needs of professional firms, who require a platform to share their resources. For now the webapp only the allows the user to upload videos/images/documents, other media types woul,d be allowed in the further versions. </h8>
        <br />
        <br />
        <span style={{ fontSize: '15px' }}class="text-muted">version 1.0</span>
        <br />
        <br />
      <Form>
    <Form.Group controlId="formBasicEmail">

      <Form.Control autocomplete="off" onChange={this.handleChange} type="text" placeholder="Search" />
      <Form.Text className="text-muted">
        Jump directly to your desired post.
      </Form.Text>
    </Form.Group>


    <Button variant="primary" >
      <Link id="search"to={"/searchPost/" + this.state.Query}>Submit</Link>
    </Button>
  </Form>
</div>

          </body>);
}

}

const mapDispatchToProps = dispatch => {
	return {
		logout: () => dispatch(actions.logout())
	};
};

export default withRouter(connect(null, mapDispatchToProps)(Homepage));
