import  React, { Component } from  'react';
import  PostService  from  './PostService';

const  postService  =  new  PostService();

class  PostList  extends  Component {

constructor(props) {
    super(props);
    this.state  = {
        posts: [],
    };
}

componentDidMount() {
    var  self  =  this;
    const postService = new PostService();
    postService.getPosts().then(function (result) {
        console.log(result);
        self.setState({ posts:  result.data})
    });
}
render() {

  return (


         <div class="container">
           <nav class="navbar navbar-expand-lg bg-transparent">
         <a class="navbar-brand" href="/">Home</a>
         <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
           <span class="navbar-toggler-icon"></span>
         </button>
         <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
           <div class="navbar-nav">
             <a class="nav-item nav-link" href="docs.html">Docs<span class="sr-only">(current)</span></a>
             <a class="nav-item nav-link" href="upload.html">Upload</a>
           </div>
         </div>
         </nav>
<div class="dropdown">
<button type="button" class="btn dropdown-toggle" data-toggle="dropdown">
Filter by
</button>
<div class="dropdown-menu">
<a class="dropdown-item" href="#">Videos</a>
<a class="dropdown-item" href="#">Images</a>
<a class="dropdown-item" href="#">Docs</a>

</div>
</div>
<div class="row">
{this.state.posts.map( c =>
<div key={"{c.id}"} class="col-lg-8 col-md-10 mx-auto">
  <div class="post-preview">
    <a href="#">
      <h2 class="post-title">
        {c.Name}
      </h2>
      <h3 class="post-subtitle">
        {c.Description.substring(0,10) + "..."}
      </h3>
    </a>
    <p class="post-meta">Posted by
        { " " + c.Author + " " }

      on {" " + c.Init_time.substring(0,10)}</p>
  </div>
  <hr></hr>


</div>
)};
</div>

</div>
         );
  }
}
export  default  PostList;
