import  React, { Component } from  'react';
import  PostService  from  '../Action/PostService';
import {Link} from 'react-router-dom'
import {Card,Nav,Navbar,NavDropdown} from 'react-bootstrap'
import {Form,Button} from 'react-bootstrap'
const  postService  =  new  PostService();

class  Login  extends  Component {
  constructor(props) {
      super(props);
      this.state = {
        email:'',
        password:''
        };
  }


  componentDidMount() {
    var self = this;

  }
  handleChange = (e) => {
    this.setState({
      [e.target.id] : e.target.value
    })

  };

  handleSubmit = (e) => {


      postService.login(this.state)
e.preventDefault()

  }

  render() {
    return(
      <div class="container login-form">
        <h1>Login</h1>
        <br/>
          <Form onSubmit={this.handleSubmit}>
            <Form.Group >

              <Form.Control onChange={this.handleChange}class="login_credentials" type="text" id="username"placeholder="Username" />
            </Form.Group>
            <Form.Group >

              <Form.Control onChange={this.handleChange}class="login_credentials" type="password" id="password" placeholder="Password" />
            </Form.Group>

  <Button variant="primary" type="submit">
    Submit
  </Button>
          </Form>

      </div>

    );

}

}

export  default  Login;
