import  React, { Component } from  'react';
import  PostService  from  './PostService';

const  postService  =  new  PostService();


class  UploadPost  extends  Component {




constructor(props) {
    super(props);

    this.state = {
      Name: "",
      Init_time: "",
      Author: "",
      Description: "",
      Video: null,
      Image: null,
      Doc: null

      };
this.handleSubmit = this.handleSubmit.bind(this)
}

componentDidMount() {

}

handleChange = (e) => {
  this.setState({
    [e.target.id]: e.target.value
  })
};

handleFileChange = (e) => {
  this.setState({
  [e.target.id]: e.target.files[0]
  })
};

handleSubmit = (e) => {
  var today = new Date()
  var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate() + "T" + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() +"Z";

  let form_data = new FormData();

  form_data.append('Name', this.state.Name);
  form_data.append('Init_time', date);
  form_data.append('Author', this.state.Author);
  form_data.append('Description', this.state.Description);
  if (this.state.Video != null) {
    form_data.append('Video', this.state.Video, this.state.Video.name);
  }
if (this.state.Image != null) {
  form_data.append('Image', this.state.Image, this.state.Image.name);

}
if (this.state.Doc != null) {
  form_data.append('Doc', this.state.Doc, this.state.Doc.name);
}


console.log(form_data)
postService.createPost(form_data)

  e.preventDefault()




};


onChangeHandler=event=>{

}

render() {

  return (
<div className="container">

        <nav className="navbar navbar-expand-lg bg-transparent">
      <a className="navbar-brand" href="/">Home</a>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
        <div className="navbar-nav">
          <a className="nav-item nav-link" href="/posts/">Docs<span className="sr-only">(current)</span></a>
          <a className="nav-item nav-link" href="/upload/">Upload</a>
        </div>
      </div>
    </nav>
      <div className="container-contact100" >
    		<div className="wrap-contact100" >
    			<div className="contact100-form-title" >
    				<span>Create a new post.</span>
    			</div>

    			<form  onSubmit={this.handleSubmit} className="contact100-form validate-form">
    				<div className="wrap-input100 validate-input">
    					<input ref="Name" autoComplete="off" id="Name" className="input100" type="text" name="title" placeholder="Title" onChange={this.handleChange}/>
    					<span className="focus-input100"></span>
    					<label className="label-input100" >
    						<span className="lnr lnr-user m-b-2"></span>
    					</label>
    				</div>
            <div className="wrap-input100 validate-input">
              <input ref="Author" autoComplete="off"id="Author"className="input100" type="text" name="Author" placeholder="Author" onChange={this.handleChange} />
              <span className="focus-input100"></span>
              <label className="label-input100" >
                <span className="lnr lnr-user m-b-2"></span>
              </label>
            </div>





    				<div className="wrap-input100 validate-input">
    					<textarea ref="Description"id="Description" className="input100" name="body" placeholder="Description" onChange={this.handleChange}></textarea>
    					<span className="focus-input100"></span>
    					<label className="label-input100 rs1" >
    						<span className="lnr lnr-bubble"></span>
    					</label>
    				</div>


            <label id="icon_inputs"style={{borderRadius: "50px" },{width:"30px"},{padding:"0"},{height:"40px"}}class="btn btn-primary custom-file-upload">
            <input style={{display:"none"}} class = "btn btn-primary custom-file-uploadicon-speech" type="file" name="file" id="Video" ref="Video"onChange={this.handleFileChange}/>
<i style= {{margin:'10px'}} class="fa fa-play fa-xs"></i>

                  </label>
<br />

                  <label  id="icon_inputs"style={{borderRadius: "50px" },{width:"30px"},{padding:"0"},{height:"40px"}} class="btn btn-primary custom-file-upload">
 <input style={{display:"none"}} class = "btn btn-primary custom-file-uploadicon-speech" type="file" name="file" id="Image" ref="Image"onChange={this.handleFileChange}/>

                            <i style= {{margin:'10px'}} class="fa fa-image fa-xs"></i>
                        </label>
<br />
                        <label id="icon_inputs"style={{borderRadius: "50px" },{width:"30px"},{padding:"0"},{height:"40px"}} class="btn btn-primary custom-file-upload">
 <input style={{display:"none"}} class = "btn btn-primary custom-file-uploadicon-speech" type="file" name="file" id="Doc" ref="Doc"onChange={this.handleFileChange}/>
                                <i style= {{margin:'10px'}} class="fa fa-file fa-xs"></i>
                              </label>



    				<div className="container-contact100-form-btn">

    					<input type='submit' value='Post'className="contact100-form-btn">

    					</input>
    				</div>


    			</form>
    		</div>
    	</div>
      <form>
  <div class="form-row">
    <div  style={{margin:10}} class="form-group col-md-5">
      <label for="inputEmail4">Tile</label>
      <input type="email" class="form-control" id="inputEmail4"></input>
    </div>
    <div  style={{margin:10}} class="form-group col-md-6">
      <label for="inputPassword4">Author</label>
      <input type="password" class="form-control" id="inputPassword4"></input>
    </div>
  </div>
  <div style={{margin:10}}  class="form-group">
    <label for="inputAddress">Description</label>
    <input type="text" class="form-control" id="inputAddress" placeholder="1234 Main St"></input>
  </div>
  <div style={{margin:0}}  class="form-row">
    <div style={{margin:10}} class="form-group col-md-3 custom-file">
    <input type="file" class="custom-file-input" id="customFile" />
    <label class="custom-file-label" for="customFile">Choose file</label>
    </div>
    <div style={{margin:10}} class="form-group col-md-3 custom-file">
    <input type="file" class="custom-file-input" id="customFile" />
    <label class="custom-file-label" for="customFile">Choose file</label>
    </div>
    <div style={{margin:10}} class="form-group col-md-3 custom-file">
    <input type="file" class="custom-file-input" id="customFile" />
    <label class="custom-file-label" for="customFile">Choose file</label>
    </div>
  </div>

  <button type="submit" class="btn btn-primary">Sign in</button>
</form>

</div>
