import  React, { Component } from  'react';
import  PostService  from  './PostService';

const  postService  =  new  PostService();

class  ViewPost  extends  Component {

  constructor(props) {
  super(props);
  this.state  = {
      post: []
  };
}



componentDidMount(){
var  self  =  this;
const { match: { params } } = this.props;
const postService = new PostService();
postService.getPost(params.id).then(function (result) {
    self.setState({ post:  result.data})

});
}

  render() {



    return (
      <div className="container">
      <nav className="navbar navbar-expand-lg bg-transparent">
    <a className="navbar-brand" href="/posts/">Back</a>
    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>

    </nav>
      {this.state.post.map(c =>
        <div style= {{width:'70%'}} className="container">




            <div style={{margin:'0 auto'}}>


              <h1 className="mt-4">{c.Name + " "} <a href={c.Video}><i style= {{float:'right'}} class="fa fa-image"></i></a> <p>{" "}</p> <a><i style= {{float:'right'}} class="fa fa-file"></i></a></h1>


              <p className="lead">

                Posted on <strong>{c.Init_time.substring(0,10)}</strong>   by <strong>{" " + c.Author}</strong>
              </p>






        <hr />


              <p className="lead">{c.Description}</p>





              <hr />

              <br />
              <br /><br /><br />



          </div>


        </div>
      )}
      </div>
           );
}

}

export  default  ViewPost;
