import  React, { Component } from  'react';
import  PostService  from  './PostService';
import $ from 'jquery';

const  postService  =  new  PostService();


class  UploadPost  extends  Component {




constructor(props) {
    super(props);

    this.state = {
      Name: "",
      Init_time: "",
      Author: "",
      Description: "",
      Video: null,
      Image: null,
      Doc: null

      };
this.handleSubmit = this.handleSubmit.bind(this)
}

componentDidMount() {

}

handleChange = (e) => {
  this.setState({
    [e.target.id]: e.target.value
  })
};

handleFileChange = (e) => {
  this.setState({
  [e.target.ref]: e.target.files[0]
  })
};

handleSubmit = (e) => {
  var today = new Date()
  var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate() + "T" + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() +"Z";

  let form_data = new FormData();

  form_data.append('Name', this.state.Name);
  form_data.append('Init_time', date);
  form_data.append('Author', this.state.Author);
  form_data.append('Description', this.state.Description);
  if (this.state.Video != null) {
    form_data.append('Video', this.state.Video, this.state.Video.name);
  }
if (this.state.Image != null) {
  form_data.append('Image', this.state.Image, this.state.Image.name);

}
if (this.state.Doc != null) {
  form_data.append('Doc', this.state.Doc, this.state.Doc.name);
}


console.log(form_data)
postService.createPost(form_data)

  e.preventDefault()




};


onChangeHandler=event=>{

}

render() {

  return (

  <div>
      <form  onSubmit={this.handleSubmit}>
  <div class="form-row">
    <div   class="form-group col-md-5">
      <label for="inputEmail4">Tile</label>
      <input type="text" id="Name" class="form-control" id="inputEmail4" onChange={this.handleChange}></input>
    </div>
    <div  class="form-group col-md-6">
      <label for="inputPassword4">Author</label>
      <input type="text"id="Author" class="form-control" id="inputPassword4" onChange={this.handleChange}></input>
    </div>
  </div>
  <div class="form-group">
    <label for="inputAddress">Description</label>
    <input type="text"id=""  class="form-control" id="inputAddress" placeholder="1234 Main St"onChange={this.handleChange}></input>
  </div>
  <div style={{marginLeft:-10}} class="form-row">
    <div style={{margin:10}} class="form-group col-md-3 custom-file">
    <input ref="Video" type="file" class="custom-file-input" id="customFile" onChange={this.handleFileChange}></input>
    <label class="custom-file-label" for="customFile">Choose file</label>
    </div>

    {$(".custom-file-input").on("change", function() {
  var fileName = $(this).val().split("\\").pop();
  $(this).siblings(".custom-file-label").addClass("selected").html(fileName);
})}
  </div>
  <button type="submit" name="button">Submit</button>


</form>
</div>

         );

  }
}
export  default  UploadPost;
