import  React, { Component } from  'react';
import  PostService  from  './PostService';
import $ from 'jquery';
import {Form,Col,Button,Navbar,Nav,NavDropdown} from 'react-bootstrap'
import bsCustomFileInput from 'bs-custom-file-input'

const  postService  =  new  PostService();


class  UploadPost  extends  Component {




constructor(props) {
    super(props);

    this.state = {
      Name: "",
      Init_time: "",
      Author: "",
      Description: "",
      Video: null,
      Image: null,
      Doc: null

      };
this.handleSubmit = this.handleSubmit.bind(this)
}

componentDidMount() {

  $(document).ready(function () {
    bsCustomFileInput.init()
  })
}

handleChange = (e) => {
  this.setState({
    [e.target.id]: e.target.value
  })
  console.log("blah")
};

handleFileChange = (e) => {
  this.setState({
  [e.target.id]: e.target.files[0]

  })
  console.log(e.target.id)
};

handleSubmit = (e) => {
  var today = new Date()
  var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate() + "T" + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() +"Z";

  let form_data = new FormData();

  form_data.append('Name', this.state.Name);
  form_data.append('Init_time', date);
  form_data.append('Author', this.state.Author);
  form_data.append('Description', this.state.Description);
  if (this.state.Video != null) {
    form_data.append('Video', this.state.Video, this.state.Video.name);
  }
if (this.state.Image != null) {
  form_data.append('Image', this.state.Image, this.state.Image.name);

}
if (this.state.Doc != null) {
  form_data.append('Doc', this.state.Doc, this.state.Doc.name);
}


console.log(this.state)
postService.createPost(form_data)

  e.preventDefault()




};


onChangeHandler=event=>{

}

render() {

  return (

  <div className="container">
  <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#features">Features</Nav.Link>
      <Nav.Link href="#pricing">Pricing</Nav.Link>
      <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
      </NavDropdown>
    </Nav>
    <Nav>
      <Nav.Link href="#deets">More deets</Nav.Link>
      <Nav.Link eventKey={2} href="#memes">
        Dank memes
      </Nav.Link>
    </Nav>
  </Navbar.Collapse>
</Navbar>
  <Form style={{marginTop:30}}onSubmit =  {this.handleSubmit}>
    <Form.Row>
      <Form.Group as={Col} >
        <Form.Label>Title</Form.Label>
        <Form.Control type="text" placeholder="Enter email" id="Name"
        onChange = {this.handleChange} />
      </Form.Group>

      <Form.Group as={Col} >
        <Form.Label>Author</Form.Label>
        <Form.Control type="text" placeholder="Password" id="Author"
        onChange = {this.handleChange}/>
      </Form.Group>
    </Form.Row>

    <Form.Group >
      <Form.Label>Description</Form.Label>
      <Form.Control placeholder="1234 Main St" id="Description"
      onChange = {this.handleChange}/>
    </Form.Group>



    <Form.Row style={{marginTop:30}}>
      <Form.Group as={Col} controlId="formGridCity">
      <Form.File
      onChange = {this.handleFileChange}
         id="Video"
         label="Video"
         data-browse="Add"
         custom
         ref = "Video"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridState">
      <Form.File
      onChange = {this.handleFileChange}
         id="Image"
         label="Image"
         data-browse="Add"
         custom
         ref = "Image"
       />
      </Form.Group>

      <Form.Group as={Col} controlId="formGridZip">
      <Form.File
        onChange = {this.handleFileChange}
         id="Doc"
         label="Docs"
         data-browse="Add"
         custom
         ref = "Doc"
       />
      </Form.Group>
    </Form.Row>


    <Button variant="primary" type="submit">
      Submit
    </Button>
  </Form>
</div>

         );

  }
}
export  default  UploadPost;
