import  React, { Component } from  'react';
import  PostService  from  '../Action/PostService';
import {Link} from 'react-router-dom'
import {Card,Nav,Navbar,NavDropdown} from 'react-bootstrap'
import {Form,Button} from 'react-bootstrap'
const  postService  =  new  PostService();

class  Login  extends  Component {
  constructor(props) {
      super(props);
      this.state = {
        email:'',
        password:''
        };
  }


  componentDidMount() {
    var self = this;

  }
  handleChange = (e) => {
    this.setState({
      [e.target.id] : e.target.value
    })
    console.log(this.state)
  };

  render() {
    return(
      <div class="container login-form">
        <h1>Login</h1>
        <br/>
          <Form>
            <Form.Group controlId="formGroupEmail">

              <Form.Control onChange={this.handleChange}class="login_credentials" type="email" id="email"placeholder="Enter email" />
            </Form.Group>
            <Form.Group controlId="formGroupPassword">

              <Form.Control onChange={this.handleChange}class="login_credentials" type="password" id="password" placeholder="Password" />
            </Form.Group>
          </Form>

      </div>

    );

}

}

export  default  Login;
